import React from 'react'

// En React, on dit qu'un component rend un élément graphique.
// La classe React.Component implémente une méthode render.
// C'est cette méthode qui va définir ce que notre component va rendre à l'écran.
//Lorsque l'on crée un component custom, on doit obligatoirement réimplémenter cette méthode et retourner (return) les éléments graphiques,
// sans quoi notre component ne rendra rien et ne fonctionnera pas.
class Card extends React.Component {
  constructor(props) {
    console.log("RENDER")
    super(props)
  }
  render() {
    return (
      <View style={styles.main_container}>
          <p>Salut</p>
      </View>
      // En JSX, cela donne
      //React.createElement(View, {},
      //  	React.createElement(TextInput, {placeholder: "Titre du film"}),
      //    React.createElement(Button, {title: "Rechercher", onPress: () => {}})
      //)
    )
  }
}

const styles = StyleSheet.create({
  main_container: {
    flex: 1,
    marginTop: 100
  },
  textinput: {
    marginLeft: 5,
    marginRight: 5,
    marginBottom: 20,
    height: 50,
    borderColor: '#000',
    borderWidth: 2,
    paddingLeft: 5
  }
})


export default Card
// On exporte toujours par défaut les components, c'est une autre convention de codage.
// C'est assez justifié, puisque l'on crée un fichier par component et que l'on n'exporte que celui-ci.


