import React from 'react'
import { Icon } from 'antd'
import styled from 'styled-components'
import { Col, Layout } from 'antd';

// En React, on dit qu'un component rend un élément graphique.
// La classe React.Component implémente une méthode render.
// C'est cette méthode qui va définir ce que notre component va rendre à l'écran.
//Lorsque l'on crée un component custom, on doit obligatoirement réimplémenter cette méthode et retourner (return) les éléments graphiques,
// sans quoi notre component ne rendra rien et ne fonctionnera pas.
class Card extends React.Component {
  constructor(props) {
    console.log("RENDER")
    super(props)
  }
  _hexToRgbA(hex){
    var c;
    if(/^#([A-Fa-f0-9]{3}){1,2}$/.test(hex)){
        c= hex.substring(1).split('');
        if(c.length== 3){
            c= [c[0], c[0], c[1], c[1], c[2], c[2]];
        }
        c= '0x'+c.join('');
        return 'rgba('+[(c>>16)&255, (c>>8)&255, c&255].join(',')+',1)';
    }
    throw new Error('Bad Hex');
    _hexToRgbA('#fbafff')
}
  render() {
    return (
      <Container className="card">
        <Col span={8}>
          <Figure style={{background:this.props.color}}>
            <Icon type={this.props.type} theme="outlined" style={{color: this.props.color}}/>
          </Figure>
        </Col>
        <Col span={16}>
          <Price>{this.props.price}€</Price>
          <span>{this.props.text}</span>
        </Col>
      </Container>
    )
  }
}

const Container = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 10px;
  width: 100%;
  height: 100px;
  background: #FFFFFF;
  border: 1px solid rgba(192, 196, 207, 0.3);
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.03);
  border-radius: 12px;
  margin-top: 22px;
  margin-bottom: 22px;
  margin-right: 18px;
`;

const Figure = styled.figure`
  width: 36px;
  height: 36px;
  mix-blend-mode: normal;
  border-radius: 10px;
`;

const Price = styled.h4`
  font-size: 20px;
  line-height: 32px;
  color: #171725;
  margin-bottom: 0;
`;

const Text = styled.span`
  font-size: 14px;
  line-height: 16px;
  letter-spacing: 0.1px;
`;

export default Card
